window.onload = function(){
    document.addEventListener("click", documentActions);

    function documentActions(e){
        const targetElement = e.target;
        if (window.innerWidth>768 && isMobile.any()){
            if (targetElement.classList.contains('menu__arrow')){
                targetElement.closest('.menu__item').classList.toggle('_hover');
            }
            if (!targetElement.closest('.menu__item') && (document.querySelectorAll('.menu__item._hover').length > 0)){
                for (var i = 0; i < document.querySelectorAll('.menu__item._hover').length; i++) {
                    document.querySelectorAll('.menu__item._hover')[i].classList.remove('_hover');
                }
            }
            if (targetElement.classList.contains('section-filter__section-arrow')){
                targetElement.closest('.section-filter__section').classList.toggle('_hover');
            }
            if (!targetElement.closest('.section-filter__section-with-sublist') && (document.querySelectorAll('.section-filter__section-with-sublist._hover').length > 0)){
                for (var i = 0; i < document.querySelectorAll('.section-filter__section._hover').length; i++) {
                    document.querySelectorAll('.section-filter__section-with-sublist')[i].classList.remove('_hover');
                }
            }
        }
    }
    
    // header
    const headerElement = document.querySelector('.header');
    const callback = function (entries, observer){
        if (entries[0].isIntersecting){
            headerElement.classList.remove('__scroll');
        } 
        else{
            headerElement.classList.add('__scroll');
        }
    }
    const headerObserber = new IntersectionObserver(callback);
    headerObserber.observe(headerElement); 
  
   // let containerElement = document.querySelector('.header__logo');
   //if (location.pathname = 'index.html') containerElement.style.display = 'none'; 
}
